		stat.DataQuality = CommonLib.Math.Round(test2, 2);

		DebugLib.Logger.WriteInfoLine("Rounded data quality: " + stat.DataQuality + " for supplier " + s.UserName + " with status: " + s.SupplierStatus);

		if s.DataAvailability == null {
		    Domain.DataAvailability supDataAvailability;
		    supDataAvailability.Supplier = s;
		}

		//Data Availability
		if s.Transactions.Any(a=> a.Orders.Length > 0) {
		     s.DataAvailability.Orders = true;
		}
		else {
			s.DataAvailability.Orders = false;
		}
		if s.Transactions.Any(b=> b.DeliveryNotes.Length > 0) {
		    s.DataAvailability.DeliveryNotes = true;
		}
		else {
		}
		if s.Transactions.Any(c=> c.Invoices.Length > 0) {
		     s.DataAvailability.Invoices = true;
		}
		else {
		}
		if s.Transactions.Any(a=> a.ProofOfDeliveries.Length > 0) {
		     s.DataAvailability.PoDs = true;
		}
		else {
		}
		if s.Transactions.Any(a=> a.Intermediaries.Length > 0) {
		     s.DataAvailability.IntermediaryLogistics = true;
		}
