static function Collection[Domain.SupplierStatistic] GetSupplierStatistics()
{
	Collection[Domain.SupplierStatistic] stats;
	Collection[Domain.Supplier] suppliers = Domain.Supplier.Find(s=> s.SupplierStatus == "ActiveSupplier");

	int i=0;
	foreach Domain.Supplier: sup in suppliers {
	    Domain.SupplierStatistic stat;
	    stat.Supplier = sup;
	    stat.TotalCommercialAgreements = sup.CommercialAgreements.Length;
	    stat.OnTimeDeliveries = Domain.ProofOfDelivery.Find(a=> a.Transaction.Supplier == sup && a.DeliveryStatus == "OnTime").Length;
	    stat.AsOrderedDeliveries = Domain.ProofOfDelivery.Find(a=> a.Transaction.Supplier == sup && a.QualityStatus == "Asordered").Length;
	    stat.InFullDeliveries = Domain.ProofOfDelivery.Find(a=> a.Transaction.Supplier == sup && a.QuantityStatus == "Infull").Length;
	    stat.TotalImpeachments = Domain.ProofOfDelivery.Find(a=> a.Transaction.Supplier == sup && (a.DeliveryStatus != "OnTime" || a.QualityStatus != "Asordered" || 
	    															a.QuantityStatus != "Infull")).Length;
	    stat.TotalTransactions = Domain.Transaction.Find(t=> t.Supplier == sup).Length;
	    stat.CurrentTurnover = sup.SupplierTurnover.PlannedCurrentYear;

		i= i+1;

//		DebugLib.Logger.WriteWarnLine("supplier " + sup.UserName + " OnTime " + stat.OnTimeDeliveries.ToString());
		stats.Add(stat);
	}

	return stats;
}

static function Collection[Domain.SupplierStatistic] GetSupplierTurnover()
{
	Collection[Domain.SupplierStatistic] stats;
	Collection[Domain.Supplier] suppliers = Domain.Supplier.Find(s=> s.SupplierStatus == "ActiveSupplier");

	int i=0;
	foreach Domain.Supplier: sup in suppliers {
	    Domain.SupplierStatistic stat;
	    stat.Supplier = sup;
	    stat.TotalCommercialAgreements = sup.CommercialAgreements.Length;
		stat.CurrentTurnover = sup.SupplierTurnover.PlannedCurrentYear;
		stat.NextTurnover = sup.SupplierTurnover.PlannedNextYear;
		stat.PreviousTurnover = sup.SupplierTurnover.ActualPreviousYear;

		i= i+1;

//		DebugLib.Logger.WriteWarnLine("supplier " + sup.UserName + "Current Turnover " + stat.CurrentTurnover.ToString());
		stats.Add(stat);
	}

	return stats;

}

static function Collection[Domain.SupplierStatistic] Get5MostProfitableSuppliers()
{

    Collection[Domain.SupplierStatistic] stats = Domain.SupplierStatistic.GetSupplierStatistics();
	return stats.OrderByDescending(a=> a.CurrentTurnover).Take(5);
}

function bool IsInRole(string roleName)
{
	return this.Roles.Any(r => r.Name == roleName);
}

function bool HasPermission(string permission)
{
  bool hasPermissionfromRoles = this.Roles.Any(rr => rr.Permissions.Any(pp => pp.Name == permission));
	return hasPermissionfromRoles || this.Permissions.Any(pp => pp.Name == permission);
}

function bool IsSupplier()
{
//	Domain.Supplier sup = Domain.Supplier.Find(a=> a.UserName == userName).First();
	if (this.IsInRole("GuestSupplier") ||
		this.IsInRole("PendingRegistered") ||
		this.IsInRole("RegisteredSupplier") ||
		this.IsInRole("PendingValidated") ||
		this.IsInRole("ValidatedSupplier") ||
		this.IsInRole("ActiveSupplier")) {
	    return true;
	}
	return false;
}

function bool IsInRole(string roleName)
{
	return this.Roles.Any(r => r.Name == roleName);
}

function bool HasPermission(string permission)
{
  bool hasPermissionfromRoles = this.Roles.Any(rr => rr.Permissions.Any(pp => pp.Name == permission));
	return hasPermissionfromRoles || this.Permissions.Any(pp => pp.Name == permission);
}
